  share_target, insert_time, update_time
  )
values (#{id,jdbcType=INTEGER}, #{userid,jdbcType=INTEGER}, #{articleId,jdbcType=VARCHAR}, 
  #{remark,jdbcType=VARCHAR}, #{originalUrl,jdbcType=VARCHAR}, #{shareType,jdbcType=VARCHAR}, 
  #{shareTarget,jdbcType=VARCHAR}, #{insertTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )
